import React from 'react';

const styles = {
  lainlines: {width:'30%',
              height: '30%'}
}

let img = () => (
  <img src="../images/lainlines.gif" style={styles.lainlines}/>
)

const buttonHandler = () => (
  button.getAttribute('src') === '../images/lainlines.gif' && button.src('../images/bluelines.gif')
)

let button = () => (
  <button src={'../images/lainlines.gif'} onClick={buttonHandler}>Click to switch image</button>
)

const General17 = () => (
  <div>
    <h2>Inline conditionals</h2>


  </div>
);

export default General17;
