import React from 'react';

class generalPractice17 extends React.Component{
  constructor(){
    super();

    this.state = {imageUrl: '../images/lainlines.gif',
                  switchImageUrl: '../images/bluelines.gif',
                  buttonClicked: 0};
  }

  styles = {
      lainlines: {width:'30%',
                  height: '30%',
                  display: 'none'},
    };

  buttonHandler = (img) => {
    img.src === this.state.imageURL && (img.src = this.setState());
    console.log(img.src === this.state.imageURL);
  };

  showImgHandler = () => {

    const checkAfter = () => {if(this.state.buttonClicked === 3)
                                e.target.style.display = 'none'};

    this.setState(prevState => {buttonClicked: ++prevState.buttonClicked}, checkAfter);
    console.log(this.state.buttonClicked);
  }

  render(){
    return (
      <div>
        <button onClick={this.showImgHandler}>Click 3x to show image</button>
        {this.state.buttonClicked >= 3 &&
           <img src={this.state.imageUrl} style={this.styles.lainlines}/>}
      </div>
    );
  }
}

export default generalPractice17;
