class generalPractice17 extends React.Component{
  constructor(){
    super();

    this.state = {imageUrl: '../images/lainlines.gif',
                  switchImageUrl: '../images/bluelines.gif'};
  }

  styles = {
      lainlines: {width:'30%',
                  height: '30%'}
    };

  buttonHandler = (imgae) => {
    img.src === this.state.imageURL && (img.src = this.setState());
    console.log(img.src === this.state.imageURL);
  };

  render(){
    return (
      <div>
        <img src={this.state.imageDefault} style={this.styles.lainlines}/>
        <button onClick={this.buttonHandler(img)}>Click to switch image</button>
      </div>
    );
  }
}

export default generalPractice17;
