import React from 'react';

class generalPractice17 extends React.Component{
  constructor(){
    super();

    this.state = {imageUrl: '../images/lainlines.gif',
                  switchImageUrl: '../images/bluelines.gif'};
  }

  styles = {
      lainlines: {width:'30%',
                  height: '30%'}
    };

  LainImg = () => (
    <img src={this.state.imageUrl} style={this.styles.lainlines}/>
  );

  buttonHandler = () => {
    this.LainImg.src === this.state.imageURL && (this.LainImg.src = this.state.switchImageURL)
  };

  LainButton = () => (
    <button onClick={this.buttonHandler()}>Click to switch image</button>
  );

  render(){
    return (
      <div>
        {this.LainImg()}
        {this.LainButton()}
      </div>
    );
  }
}

export default generalPractice17;
