import React from 'react';

// Render either <LoginButton /> or <LogoutButton /> depending on its current state. It will also render a <Greeting /> from the previous example:
class ConditionalRender extends React.Component{
  constructor(){
    super();

    this.state = { loggedIn: false };

    this.styles = {buttonContainer:{
      marginLeft: '50%'
    }}
  }

  logout() { this.setState({loggedIn: false}) };
  login() { this.setState({loggedIn: true}) };

  render(){
    let button;

    if(this.state.loggedIn)
      button = <button onClick={() => this.logout()}>aaaaa</button>;
    else
      button = <button onClick={() => this.login()}>Log In</button>;

    return (
      <div className="buttonContainer" style={this.styles.buttonContainer}>
        {button}
        <p>{this.state.loggedIn.toString()}</p>
      </div>
    );
  }
}

export default ConditionalRender;
