import React from 'react';

class generalPractice17 extends React.Component{
  constructor(){
    super();

    this.state = {imageUrl: '../images/lainlines.gif',
                  switchImageUrl: '../images/bluelines.gif',
                  buttonClicked: 0};
  }

  styles = {
      lainlines: {width:'30%',
                  height: '30%',
                  display: 'none'},
    };

  showImgHandler = (e) => {

    const target = e.target;

    this.setState(prevState => {buttonClicked: prevState.buttonClicked++},
      () => {if(this.state.buttonClicked === 3) target.style.display = 'visible'; console.log(this.state.buttonClicked)});

  }

  render(){
    console.log(this.state.buttonClicked);
    return (
      <div>
        <button onClick={this.showImgHandler}>Click 3x to show image {this.state.buttonClicked}</button>

        {this.state.buttonClicked === 3 &&
           <img src={this.state.imageUrl} style={this.styles.lainlines}/>}
      </div>
    );
  }
}

export default generalPractice17;
