import React from 'react';

const styles = {
  pre: {fontSize: '1.4rem'},
  container: {margin: '0 0 10px 0'}
}

const MarkedCode = ({code, codeID, type}) => (
  <div style={styles.container}>
      <pre className={type} key={codeID} style={styles.pre}><code>
        {code}
      </code>
    </pre>
  </div>
);

export default MarkedCode;
