import React from 'react';

class GeneralPractice18 extends React.Component{
  constructor(props){
    super(props);

    this.state = {image1: 'boxA.gif',
                  image2: 'boxB.gif'};
  }

  styles = {
    duoImage: {width: '220px',
               height: '200px'}
  }

  imageHandler = (e) => {
    const id = e.target.key;
    e.target.src.includes('boxA.gif') ? this.setState({id: 'boxA.gif'}) : this.setState({id: 'boxB.gif'});
  }

  render(){
    return (
        <div>
          <img key='image1' src={this.state.image1} onClick={this.imageHandler} style={this.styles.duoImage}></img>;
          <img key='image2' src={this.state.image2} onClick={this.imageHandler} style={this.styles.duoImage}></img>;
        </div>
      );
  }
}





export default GeneralPractice18;
