const codeBlocks = [
    {codeId: "testId1",
      title: "Modified CodeGenerator State",
      type: "language-javascript",
      code:
      `       //Instead of defining state inside CodeGenetor, state is now passed in from file by root Component
      //Also coupled header with codeBlock, set by passing in 'title' prop
      class CodeGenerator extends React.Component{
        constructor(props){
          super(props);

          this.state = {
            codeBlocks: this.props.codeBlocks.map(block =>
                            ({codeId: block.codeID,
                              type: block.type,
                              code: block.code,
                              title: block.title}))
          };

          this.styles = {
            h2: { color: '#32DD72',
                  textAlign: 'left',
                  margin: '20px 0 0 0'}
          }
        }

        render(){
          console.log(this.state.codeBlocks);
          const {codeId, type, code, title} = this.state.codeBlocks;

          return this.state.codeBlocks.map(block =>
          (
            <div>
              <h2 style={this.styles.h2}>{block.title}</h2>
              <MarkedCode codeID={block.codeId} code={block.code} type={block.type} />
            </div>
          ));
          // return <div></div>
        }
      }`
    },
    {codeId: "testId2",
      title: "test2",
      type: "language-javascript",
      code: "function()test2"
    }
];

export default codeBlocks;
